import React, { useEffect, useState } from "react";
import {Link, useHistory} from "react-router-dom"
import { createDeck } from "../utils/api/index";

export function CreateDeck(){
    const [newDeck, setNewDeck] = useState({})
    const history = useHistory();
    const handleChange = (event) => {
    const { name, value } = event.target;
    setNewDeck({...newDeck, [name]: value})
   }
   const handleSubmit = (event) => {
    event.preventDefault();
    async function callCreateDeck() {
        try {
          const deck = await createDeck(newDeck, abortController.signal);
          history.push(`/decks/${deck.id}`);
        } catch (err) {
          if (err.name === "AbortError") {
            console.info("aborted");
          } else {
            throw err;
          }
        }
      }
      callCreateDeck();

      return () => {
        abortController.abort();
      };
    }


    return (<>
    <nav aria-label="breadcrumb">
        <ol class="breadcrumb">
            <li class="breadcrumb-item"><Link to={`/`}>Home</Link></li>
            <li class="breadcrumb-item active" aria-current="page">Create Deck</li>
        </ol> 
    </nav>
    <div class="mb-3">
  <label for="exampleFormControlInput1" class="form-label">Name</label>
  <input 
    type="text" 
    class="form-control" 
    name="name"
    id="name" 
    placeholder="Deck Name"
    value={newDeck?.name}
    onChange={handleChange}/>
</div>
<div class="mb-3">
  <label for="exampleFormControlTextarea1" class="form-label">Description</label>
  <textarea 
    class="form-control" 
    name="description"
    id="description"  
    placeholder="Brief description of deck"
    value={newDeck?.description} 
    onChange={handleChange}
    rows="3"></textarea>
  <Link to={'/'} type="button" class="btn btn-secondary">Cancel</Link>
  <button type="button" onClick={handleSubmit} class="btn btn-primary">Submit</button>
</div>
    </>)
}
