import React, {useState} from "react"

export default function FlashCard ({deck = {cards: []}}){
    const [front, setFront] = useState(true)
    const {cards} = deck;
    let cardId = 0;

    function handleFlip(){
        front === true? setFront(false): setFront(true);
    }

    const view = front? <p class="card-text">{cards[cardId].front}</p> : <p class="card-text">{cards[cardId].back}</p>
    return (<>
            <div className="card">
            <div className="card-body">
            <h5 className="card-title">Card {cardId + 1 } of {deck.cards.length}</h5>
                {view}
                <button type="button" onClick={handleFlip} className="btn btn-secondary">Flip</button>
            </div>
            </div>
            </>)
}
