import React, {useState} from "react"

export default function FlashCard ({deck = {cards: []}}){
    const [front, setFront] = useState(true)
    const {cards} = deck;
    let cardId = 0;

    return (<>
            <div class="card">
            <div class="card-body">
            <h5 class="card-title">Card {cardId + 1 } of {deck.cards.length}</h5>
                <p class="card-text">{cards[cardId].front}</p>
                <button type="button" class="btn btn-secondary">Flip</button>
            </div>
            </div>
            </>)
}
